#!/usr/bin/env ruby require "#{ENV['TM_SUPPORT_PATH']}/lib/plist" file = File.expand_path('~/Library/Preferences/com.macromates.textmate.latex_config.plist') if !FileTest.exist?(file) then file = ENV['TM_BUNDLE_SUPPORT'] + "/latex_config.plist" end
File.open(file) do |f| @plist = PropertyList::load(f) end shortcuts = @plist['environments'] lshortcuts = @plist['labeled_environments'] if ENV['TM_SCOPE'].match('beamer') then shortcuts.update(@plist['environments_beamer']) lshortcuts.update(@plist['labeled_environments_beamer']) end name = STDIN.read cmd = nil for key in shortcuts.keys do if shortcuts[key]['triggers'].index(name) then cmd = key setting = :no_label dict = shortcuts end end for key in lshortcuts.keys do if lshortcuts[key]['triggers'].index(name) then cmd = key setting = :label dict = lshortcuts end end if !cmd.nil? then print "\begin{#{cmd}}" print "
" if setting == :no_label print dict[cmd]["content"] print "
\end{#{cmd}}" elsif name == "" print "\begin{${1:env}}
	$0
\end{${1:env}}" else print "\begin{#{name}}
	$0
\end{#{name}}" end
